    static void SpinningRCTrackRotationControlToggle(
        PaintSession& session, const Ride& ride, uint8_t trackSequence, uint8_t direction, int32_t height,
        const TrackElement& trackElement, SupportType supportType)
    {
        static constexpr uint32_t imageIds[4] = {
            SPR_TRACKS_SPINNING_TRACK_CONTROL_TOGGLE + 0,
            SPR_TRACKS_SPINNING_TRACK_CONTROL_TOGGLE + 1,
            SPR_TRACKS_SPINNING_TRACK_CONTROL_TOGGLE + 2,
            SPR_TRACKS_SPINNING_TRACK_CONTROL_TOGGLE + 3,
        };

        auto imageId = session.TrackColours.WithIndex(imageIds[direction]);
        PaintAddImageAsParentRotated(session, direction, imageId, { 0, 0, height }, { { 0, 6, height }, { 32, 20, 3 } });
        if (TrackPaintUtilShouldPaintSupports(session.MapPosition))
        {
            MetalASupportsPaintSetup(session, supportType.metal, MetalSupportPlace::centre, 0, height, session.SupportColours);
        }
        PaintUtilPushTunnelRotated(session, direction, height, TunnelType::SquareFlat);
        PaintUtilSetSegmentSupportHeight(
            session,
            PaintUtilRotateSegments(
                EnumsToFlags(PaintSegment::centre, PaintSegment::topRight, PaintSegment::bottomLeft), direction),
            0xFFFF, 0);
        PaintUtilSetGeneralSupportHeight(session, height + 32);
    }

